package com.example.customnewdemo.actimport android.app.Activityimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport androidx.recyclerview.widget.StaggeredGridLayoutManagerimport com.example.customnewdemo.adapter.CustomDecorAdapterimport com.example.customnewdemo.databinding.ActivityRecyDecorBindingimport com.example.customnewdemo.utils.GridSpaceItemDecorationimport com.example.customnewdemo.utils.dip2px/** * recycleview 分割线 瀑布流的使用 * */class RecyDecorActivity : AppCompatActivity() {    companion object{        val TAG = "RecyDecorActivity"        public fun startSelf(activity: Activity) {            activity.startActivity(Intent(activity, RecyDecorActivity::class.java))        }    }    private lateinit var binding: ActivityRecyDecorBinding    private var mAdapter : CustomDecorAdapter? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        binding = ActivityRecyDecorBinding.inflate(layoutInflater)        setContentView(binding.root)        initAdapter()    }    private fun initAdapter() {        mAdapter = CustomDecorAdapter(this)        binding.rvMain.layoutManager =  StaggeredGridLayoutManager(2, StaggeredGridLayoutManager.VERTICAL)        binding.rvMain.addItemDecoration(GridSpaceItemDecoration(15.dip2px(),true).setNoShowSpace(0,0))        mAdapter.also { binding.rvMain.adapter = it }        val list = arrayListOf<String>()        for (i in 0..30) {            if (i % 2 == 0) {                list.add("测试数据测试数据测试 + $i")            }else{                list.add("测试数据测试数据测试 测试数据测试数据测试 测试数据测试数据测试 $i")            }        }        mAdapter!!.refreshData(list)    }}